import Card from './card'
import ButtonAdd from './elements/buttonAdd'
import TextBox from './elements/textBox'
import {useState} from 'react'
export default function CardList(props) {
  const [state, setState] = useState({
    cards: [],
    showTextBox: false
  })
  const buttonAddHandler = () => {
    setState(...state, {showTextBox: true})
    console.log('showTextBox',showTextBox)
  }
  const addCard = (text) => {
    setState(...state, {cards: cards.concat([text])})
    setState(...state, {showTextBox: false})
  }
  const onDeleteHandler = (key) => {
    console.log('key',key)
    setState(...state, {cards: cards.splice(key,1)})
  }  
  return (
    <div>
      <p>{props.title}</p>
      {
        state.cards.map((card, index) => {
          return <Card onDelete={onDeleteHandler} cardIndex={index} key={index} content={card}/>
        })
      }
      <ButtonAdd onClick={buttonAddHandler}/>
      {
        state.showTextBox && 
        <TextBox submitText={addCard}/>
      }
    </div>
  )
}
